package class7;

public class whilePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// odd numbers
		int c=3;
		while(c<=20) {
			System.out.print(c+" ");
			c+=3;
		}
		System.out.println();
		int d=2;
		while (d<=20) {
			System.out.print(d+" ");
			d+=2;
		}
		System.out.println();
		
		
			}
		
		
	}
	
     
      
      

